mapper.select:
select * from team_detail

    (deleted ='0' or deleted is null)

        and (
        name like "%"#{searchKey}"%"
        or type like "%"#{searchKey}"%"
        or task like "%"#{searchKey}"%"
        or professional_type like "%"#{searchKey}"%"
        )


        and identity = #{identity}


order by create_time desc limit #{offset}, #{pageSize}
